from typing import List


class InvalidUserNotificationIds(Exception):
    def __init__(self, notification_ids: List[str]):
        self.notification_ids = notification_ids


class UserDoesNotHaveUnreadNotifications(Exception):
    pass


class InvalidUserNotificationId(Exception):
    def __init__(self, notification_id: str):
        self.notification_id = notification_id


class GivenNotificationHasAlreadyReadByUser(Exception):
    def __init__(self, notification_id: str):
        self.notification_id = notification_id


class InvalidNotificationId(Exception):
    def __init__(self, notification_id: str):
        self.notification_id = notification_id


class InvalidNotificationIds(Exception):
    def __init__(self, notification_ids: List[str]):
        self.notification_ids = notification_ids


class NotificationSendingFailureException(Exception):
    pass


class InvalidNotificationCategory(Exception):
    def __init__(self, notification_category: str):
        self.notification_category = notification_category
