from ib_notifications.interactors.presenters.presenter_interface import \
    PresenterInterface
from ib_notifications.interactors.storages.storage_interface import \
    StorageInterface


class CreateGroupInteractor:
    def __init__(self, storage: StorageInterface):
        self.storage = storage

    def create_group_response(self, name: str, presenter: PresenterInterface):
        group_id = self.create_group(name=name)
        response = presenter.create_group_response(group_id=group_id)
        return response

    def create_group(self, name: str) -> int:
        group_id = self.storage.create_group(name=name)
        return group_id
