from ib_notifications.interactors.storages.storage_interface import \
    StorageInterface


class DeleteAllCloudMessageTokensInteractor:
    def __init__(self, storage: StorageInterface):
        self.storage = storage

    def delete_all_cloud_message_tokens(self, user_id: str):
        self.storage.delete_cloud_message_tokens(user_id=user_id)
