import uuid

from django.db import models


class Notification(models.Model):
    id = models.UUIDField(primary_key=True, default=uuid.uuid4)
    created_at = models.DateTimeField(verbose_name="Created At",
                                      auto_now=True)
    title = models.TextField()
    message = models.TextField()
    notification_type = models.TextField(null=True, blank=True, default="")
    extra_data = models.TextField(null=True, blank=True)
    notification_service_response = models.TextField(null=True, blank=True)

    class Meta:
        app_label = "ib_notifications"

    def __str__(self):
        return self.title


class FCMKeys(models.Model):
    id = models.UUIDField(primary_key=True, default=uuid.uuid4)
    credentials = models.TextField()
    created_at = models.DateTimeField(
        verbose_name="Created At", auto_now=True)

    class Meta:
        app_label = "ib_notifications"

