from django_swagger_utils.utils.http_response_mixin import HTTPResponseMixin

from ib_notifications.interactors.presenter_interfaces. \
    mark_all_user_notifications_as_read_presenter_interface import \
    MarkAllUserNotificationsAsReadPresenterInterface


class MarkAllUserNotificationsAsReadPresenterImplementation(
    MarkAllUserNotificationsAsReadPresenterInterface, HTTPResponseMixin
):

    def raise_user_does_not_have_unread_notifications(self):
        from ib_notifications.constants.exception_messages import \
            USER_DOES_NOT_HAVE_UNREAD_NOTIFICATIONS
        response_dict = ({
            "response": USER_DOES_NOT_HAVE_UNREAD_NOTIFICATIONS[0],
            "http_status_code": 404,
            "res_status": USER_DOES_NOT_HAVE_UNREAD_NOTIFICATIONS[1]
        })
        return self.prepare_404_not_found_response(response_dict)
