from ib_notifications.presenters.\
    mark_all_user_notifications_as_read_presenter_implementation import \
    MarkAllUserNotificationsAsReadPresenterImplementation


class TestMarkAllUserNotificationsAsReadPresenterImplementation:

    def test_raise_user_does_not_have_unread_notifications(self, snapshot):
        # Arrange
        presenter = MarkAllUserNotificationsAsReadPresenterImplementation()

        # Act
        response_object = \
            presenter.raise_user_does_not_have_unread_notifications()

        # Assert
        import json
        response = json.loads(response_object.content)
        snapshot.assert_match(response['http_status_code'], 'http_status_code')
        snapshot.assert_match(response['res_status'], 'res_status')
        snapshot.assert_match(response['response'], 'response')
