from ib_notifications.constants.config import MAX_LIMIT
from ib_notifications.constants.exception_messages import INVALID_OFFSET, \
    INVALID_LIMIT
from django_swagger_utils.drf_server.exceptions import BadRequest


class ParameterValidator:

    @staticmethod
    def validate_offset(offset: int):
        if offset < 0:
            raise BadRequest(*INVALID_OFFSET)

    @staticmethod
    def validate_limit(limit: int):
        if limit > MAX_LIMIT:
            raise BadRequest(*INVALID_LIMIT)
