from django_swagger_utils.drf_server.utils.decorator.interface_decorator \
    import validate_decorator
from .validator_class import ValidatorClass
from ...interactors.delete_user_notification import DeleteUserNotification
from ...presenters.delete_user_notification_presenter import \
    DeleteUserNotificationPresenter
from ...storages.storage_implementation import StorageImplementation


@validate_decorator(validator_class=ValidatorClass)
def api_wrapper(*args, **kwargs):
    user_id = kwargs['user_dto'].user_id
    query_params = kwargs['request_query_params']
    notification_id = query_params.notification_id

    presenter = DeleteUserNotificationPresenter()

    interactor = DeleteUserNotification(StorageImplementation())
    return interactor.delete_user_notification_wrapper(
        user_id=user_id,
        notification_id=notification_id,
        presenter=presenter
    )
