from django_swagger_utils.drf_server.utils.decorator.interface_decorator \
    import validate_decorator

from ib_notifications.interactors.unread_notifications_count_interactor import \
    UnreadNotificationsCountInteractor
from ib_notifications.presenters.presenter_implementation import \
    PresenterImplementation
from ib_notifications.storages.storage_implementation import \
    StorageImplementation
from .validator_class import ValidatorClass


@validate_decorator(validator_class=ValidatorClass)
def api_wrapper(*args, **kwargs):
    user_id = kwargs['user_dto'].user_id

    storage = StorageImplementation()
    presenter = PresenterImplementation()
    interactor = UnreadNotificationsCountInteractor(storage=storage)

    return interactor.get_unread_notifications_count_response(
        user_id=user_id, presenter=presenter)
