from django.http import HttpResponse
from django_swagger_utils.drf_server.utils.decorator.interface_decorator \
    import validate_decorator
from .validator_class import ValidatorClass


@validate_decorator(validator_class=ValidatorClass)
def api_wrapper(*args, **kwargs):
    user_id = kwargs['user_dto'].user_id
    query_params = kwargs["query_params"]
    notification_id = query_params["notification_id"]

    from ib_notifications.interactors.\
        mark_a_user_notification_as_read_interactor import \
        MarkAUserNotificationAsReadInteractor
    from ib_notifications.storages.storage_implementation import \
        StorageImplementation

    storage = StorageImplementation()
    interactor = MarkAUserNotificationAsReadInteractor(
        storage=storage
    )

    from ib_notifications.presenters.\
        mark_a_user_notification_as_read_presenter_implementation import \
        MarkAUserNotificationAsReadPresenterImplementation

    presenter = MarkAUserNotificationAsReadPresenterImplementation()
    response = interactor.mark_a_user_notification_as_read_wrapper(
        user_id=user_id, notification_id=notification_id, presenter=presenter
    )

    return response
