"""
Mark Unread Notifications As Read
"""

from django_swagger_utils.utils.test import CustomAPITestCase

from ib_notifications.models import UserNotification
from ib_notifications.utils.api_test_utils import ApiTestUtils
from . import APP_NAME, OPERATION_NAME, REQUEST_METHOD, URL_SUFFIX

REQUEST_BODY = """
{}
"""

TEST_CASE = {
    "request": {
        "path_params": {},
        "query_params": {},
        "header_params": {},
        "securities": {"oauth": {"tokenUrl": "http://www.example.com",
                                 "flow":
                                     "password", "scopes": ["write", "update"],
                                 "type": "oauth2"}},
        "body": REQUEST_BODY,
    },
}


class TestCase01MarkUnreadNotificationsAsReadAPITestCase(ApiTestUtils):
    app_name = APP_NAME
    operation_name = OPERATION_NAME
    request_method = REQUEST_METHOD
    url_suffix = URL_SUFFIX
    test_case_dict = TEST_CASE

    def setupUser(self, username, password):
        super(TestCase01MarkUnreadNotificationsAsReadAPITestCase,
              self).setupUser(
            username=username,
            password=password
        )

        import factory
        from ib_notifications.tests.factories.models import \
            UserNotificationFactory, NotificationFactory
        UserNotificationFactory.reset_sequence()
        NotificationFactory.reset_sequence()

        notification_objs = NotificationFactory.create_batch(
            size=2, id=factory.Iterator(
                ["bb0a8f20-e5a1-4824-856c-7dfeed9f9a2e",
                 "bb0a8f20-e5a1-4824-856c-7dfeed9f9a2f"]
            ))
        UserNotificationFactory.create(
            user_id=self.foo_user.id,
            notification=factory.Iterator(notification_objs))

    def test_case(self):
        self.default_test_case()
        unread_notifications_count = UserNotification.objects.filter(
            user_id=str(self.foo_user.id), read_at=None).count()

        self.assert_match_snapshot(unread_notifications_count,
                                   "unread_notifications_count")
