from ib_users.interactors import dtos
from ib_users.interactors.presenters.\
    create_user_fingerprint_js_login_device_details_presenter \
    import CreateUserFingerprintJsLoginDeviceDetailsPresenter
from ib_users.interactors.storages.fingerprint_js_storage import \
    FingerprintJsStorage


class CreateUserFingerprintJsLoginDeviceDetailsInteractor:
    def __init__(
            self, fingerprint_js_storage:  FingerprintJsStorage):
        self.fingerprint_js_storage = fingerprint_js_storage

    def create_user_fingerprint_js_login_device_details_wrapper(
            self, user_login_device_details_dto:
            dtos.UserFingerPrintJsLoginDeviceDetailsDTO,
            presenter: CreateUserFingerprintJsLoginDeviceDetailsPresenter):
        self.create_user_fingerprint_js_login_device_details(
            user_login_device_details_dto)

        return presenter.get_success_response()

    def create_user_fingerprint_js_login_device_details(
            self, user_login_device_details_dto:
            dtos.UserFingerPrintJsLoginDeviceDetailsDTO):
        self.fingerprint_js_storage.\
            create_user_fingerprint_js_login_device_details(
                user_login_device_details_dto)
