import typing
from dataclasses import dataclass

from ib_users.interactors.DTOs.common_dtos import PhoneNumberDTO


@dataclass
class CreateAccountWithPhoneNumberDTO(PhoneNumberDTO):
    password: str
    username: str = None
    iso_country_code: typing.Optional[str] = None


@dataclass
class CreateAccountWithEmailDTO:
    email: str
    password: str
    username: str = None
