from typing import List

from ib_users.interactors.storages.user_profile_storage import \
    UserProfileStorage


class CheckEmailsAlreadyLinkedInteractor:

    def __init__(self, storage: UserProfileStorage):
        self.storage = storage

    def check_is_emails_already_linked_wrapper(self, email_ids: List[str]):
        self.check_is_emails_already_linked(email_ids=email_ids)

    def check_is_emails_already_linked(self, email_ids: List[str]):
        self.storage.check_is_emails_already_linked(email_ids=email_ids)
        return
