from ib_users.interactors.storages.user_accounts_storage import \
    UserAccountsStorage


class GetAllUsersAccountsInteractor:

    def __init__(self, storage: UserAccountsStorage):
        self.storage = storage

    def get_all_users_accounts_wrapper(self):
        user_accounts_dtos = self.get_all_users_accounts()
        return user_accounts_dtos

    def get_all_users_accounts(self):
        user_accounts_dtos = self.storage.get_all_users_accounts()
        return user_accounts_dtos
