from ib_users.interactors.storages.user_accounts_storage import \
    UserAccountsStorage


class GetUserIdByUsernameInteractor:

    def __init__(self, storage: UserAccountsStorage):
        self.storage = storage

    def get_user_id_by_username_wrapper(self, username: str):
        return self.get_user_id_by_username(username=username)

    def get_user_id_by_username(self, username: str):
        user_id = self.storage.get_user_id_give_username(username=username)
        return user_id
