from dataclasses import dataclass
from datetime import datetime

from ib_users.interactors.DTOs.common_dtos import PhoneNumberDTO


@dataclass
class ResetPasswordWithOTPDTO:
    otp: str
    new_password: str


@dataclass
class ResetPasswordWithOTPUsernameDTO(ResetPasswordWithOTPDTO):
    username: str


@dataclass
class ResetPasswordWithOTPEmailDTO(ResetPasswordWithOTPDTO):
    email: str


@dataclass
class ResetPasswordWithOTPPhoneNumberDTO(PhoneNumberDTO,
                                         ResetPasswordWithOTPDTO):
    pass


@dataclass
class UserContactDetailsDTO(PhoneNumberDTO):
    email: str


@dataclass
class UserTokenDTO:
    user_id: str
    expires_at: datetime
    token: str


@dataclass
class UserResetPasswordOtpPhoneNumberDTO(ResetPasswordWithOTPPhoneNumberDTO):
    user_id: str


@dataclass
class UserResetPasswordOtpEmailDTO(ResetPasswordWithOTPEmailDTO):
    user_id: str
