from django.db import models
from ib_common.models import AbstractDateTimeModel


class UserFingerprintJsLoginDeviceDetails(AbstractDateTimeModel):
    user_id = models.CharField(max_length=36)
    request_id = models.CharField(max_length=50)
    device_id = models.CharField(max_length=50)

    def __str__(self):
        return f"<UserFingerprintJsLoginDeviceDetails: {str(self.user_id)} - {self.request_id}>"


class FingerprintJsWebhookDetails(AbstractDateTimeModel):
    request_id = models.CharField(max_length=50)
    webhook_data = models.TextField()

    def __str__(self):
        return f"FingerprintJsWebhookDetails: {str(self.request_id)}"
