from ib_users.interactors.presenters.user_credentials_presenter import \
    UserCredentialsPresenter


class UserCredentialsJSONPresenter(UserCredentialsPresenter):
    def get_response_for_reset_password(self, token: str) -> dict:
        return {"token": token}

    def get_is_default_password_changed(self, is_changed: bool) -> dict:
        return {"is_default_password_changed": is_changed}
