from django.http.response import HttpResponse

from ib_users.interactors.presenters.validate_phone_number_presenter import ValidatePhoneNumberPresenter


class ValidatePhoneNumberJSONPresenter(ValidatePhoneNumberPresenter):

    def get_validate_phone_number_response(self, is_valid_phone_number: bool) -> HttpResponse:
        import json

        data = json.dumps({
            "is_valid_phone_number": is_valid_phone_number
        })

        response_object = HttpResponse(data, status=200)

        return response_object
