def divide_input_data_into_batches(input_data_list, batch_size):
    number_of_batches = (len(input_data_list) - 1) // batch_size + 1

    batch_wise_separation = []
    for iter_value in range(number_of_batches):
        index_starts_at = iter_value * batch_size
        index_ends_at = (iter_value + 1) * batch_size
        batch_wise_separation.append(
            input_data_list[index_starts_at: index_ends_at])

    return batch_wise_separation
