from django_swagger_utils.utils.test import CustomAPITestCase
from . import APP_NAME, OPERATION_NAME, REQUEST_METHOD, URL_SUFFIX

from ib_users.models import UserFingerprintJsLoginDeviceDetails

REQUEST_BODY = """
{
    "request_id": "request_id_1",
    "device_id": "device_id_1"
}
"""

TEST_CASE = {
    "request": {
        "path_params": {},
        "query_params": {},
        "header_params": {},
        "securities": {"oauth": {"tokenUrl": "http://www.example.com", "flow": "password", "scopes": ["read", "write"], "type": "oauth2"}},
        "body": REQUEST_BODY,
    },
}


class TestCase01CreateUserFingerprintJsLoginDeviceDetailsAPITestCase(CustomAPITestCase):
    app_name = APP_NAME
    operation_name = OPERATION_NAME
    request_method = REQUEST_METHOD
    url_suffix = URL_SUFFIX
    test_case_dict = TEST_CASE

    def test_case(self):
        self.default_test_case()

        login_device_details = UserFingerprintJsLoginDeviceDetails.objects.get(
            request_id="request_id_1")
        self.assert_match_snapshot(
            login_device_details.device_id, "device_id_1")
