"""
Success Case
"""
from django.test import override_settings

from ib_users.utils.custom_test_utils import CustomTestUtils

from . import APP_NAME, OPERATION_NAME, REQUEST_METHOD, URL_SUFFIX

REQUEST_BODY = """
{
    "google_access_token": "social_id_2",
    "device_id": "Device1"
}
"""

TEST_CASE = {
    "request": {
        "path_params": {},
        "query_params": {},
        "header_params": {},
        "securities": {},
        "body": REQUEST_BODY,
    },
}


class TestCase04LoginWithGoogleV1APITestCase(CustomTestUtils):
    app_name = APP_NAME
    operation_name = OPERATION_NAME
    request_method = REQUEST_METHOD
    url_suffix = URL_SUFFIX
    test_case_dict = TEST_CASE

    def setUp(self):
        from ib_users.models import UserUniqueLoginDevice
        from ib_users.models import UserAccount, UserSocialAccount
        from ib_users.constants.user_account_constants import \
            SocialAccountProviderType
        UserAccount.objects.create_user(
            user_id='0e006a83-9150-4ac1-927d-c3c5dd387581',
            username="username001", password="Qwerty123!",
            email="username001@company.com")
        user_account = UserAccount.objects.create_user(
            user_id='f389843c-be83-4e65-ba16-110ec613d257',
            username="username002", password="Qwerty123!",
            email="username002@company.com")

        UserSocialAccount.objects.create(
            user_account=user_account, social_id="social_id_2",
            provider_type=SocialAccountProviderType.GOOGLE.value)

        UserUniqueLoginDevice.objects.create(
            user_account=user_account, device_id="Device2")

    @override_settings(ENABLE_USER_DEVICES_INFORMATION=True)
    def test_case(self):
        self.default_test_case()
        from ib_users.models import UserUniqueLoginDevice, UserLoginDeviceDetails
        user_unique_login_devices = UserUniqueLoginDevice.objects.all()
        user_login_devices = UserLoginDeviceDetails.objects.all()

        user_unique_login_devices_data = [
            {
                "user_id": str(each.user_account_id),
                "device_id": each.device_id,
            }
            for each in user_unique_login_devices
        ]

        self.assert_match_snapshot(
            user_unique_login_devices_data, "user_unique_login_devices_data")

        user_login_devices_data = [
            {
                "user_id": str(each.user_account_id),
                "device_id": each.device_id,
                "access_token": each.access_token
            }
            for each in user_login_devices
        ]

        self.assert_match_snapshot(
            user_login_devices_data, "user_login_devices_data")
