# pylint: disable-all

class AbstractBoto3Service(object):

    def client(self, service_name, **kwargs):
        service_dict = {
            "cognito-identity": self.cognito_identity_client(),
        }
        return service_dict[service_name]

    def cognito_identity_client(self):
        from s3_uploader.utils.test_utils.boto3_patches. \
            cognito_identity_service import CognitoIdentityService
        return CognitoIdentityService()


boto3_service = AbstractBoto3Service().client
